$(function() {
	var a;
	$(".ilike-m").live("click",
    function() {
        if (!$.guang.dialog.isLogin()) return ! 1;
        var a = this;
        $.guang.dialog.like_say(a,
        function() {
            var b = $(a).parent().parent().find(".like-count"),
            c = parseInt(b.html().match(/\d+/)[0], 10) + 1;
            b.html(c)
        })
    });
//取消喜欢或删除商品
	var $ilikeDel = $(".ilike-del");
	//$ilikeDel.css("display","none");
	$ilikeDel.live("click",function(){
		if($.guang.dialog.isLogin()){
			var goodsId = $(this).attr("data-proid");
			var curGoodsItem = $(this).closest(".goods");
			var le = $(this).attr("data-le");
			
			if(le=='del_good') {
				
				if(confirm('您确定要删除该宝贝吗？删了就没啦！')){;
				$.ajax({
				   url: GUANGER.path+"index.php?app=share&ac=do&le="+le,
				   type : "post",
				   dataType: "json",
				   data: {
					   goodsId : goodsId,
					   themeid : GUANGER.themeid
				   },
				   success: function(data){
					   var $json = data;
					   switch($json.code){
					   case(100):
						   curGoodsItem.addClass("goods-gray");
						  break;
					   case(101):
						   alert($json.msg);
					   	break;
					   }  
				   }
				   
			});
				}
				}else{
			
			$.ajax({
				   url: GUANGER.path+"index.php?app=share&ac=do&le="+le,
				   type : "post",
				   dataType: "json",
				   data: {
					   goodsId : goodsId,
					   themeid : GUANGER.themeid
				   },
				   success: function(data){
					   var $json = data;
					   switch($json.code){
					   case(100):
						   curGoodsItem.addClass("goods-gray");
						  break;
					   case(101):
						   alert($json.msg);
					   	break;
					   }  
				   }
			});
				}
		}
				
	}); 
	
});