$(function() {
    $(".scrollable").scrollImg({
        timer: 1E4,
        startHandle: function(a) {
            a && setTimeout(function() {
                a.playlol(0);
                a.changeClass(0);
                a.autoPlay()
            },
            1E4);
            var b = null;
            $(".navi li").each(function(c) {
                $(this).unbind();
                $(this).hover(function() {
                    b != null && clearTimeout(b);
                    b = setTimeout(function() {
                        a.changeClass(c);
                        a.stopAuto();
                        a.playlol(c)
                    },
                    200)
                },
                function() {
                    b != null && clearTimeout(b);
                    a.autoPlay()
                })
            })
        }
    });

    $(".J_Prompt li").hover(function() {
        $(this).find(".title>a").addClass("on")
    },
    function() {
        $(this).find(".title>a").removeClass("on")
    });
    $(".J_Theme-tab li").each(function(a) {
        $(this).click(function() {
            $(this).addClass("on").siblings().removeClass("on");
            $(".theme:eq(" + a + ")").show().siblings(".theme").hide()
        })
    });
    var i = $.browser.msie ? 80 : 200;
    $(".J_Theme").each(function() {
        var a = $(this);
        $(this).find("li").live("mouseover mouseout",
        function(b) {
            var c = $(this);
            if (b.type == "mouseover" && !c.hasClass("on")) {
                a.find("li").not(c).attr("class", "");
                c.find(".theme-pic").animate({
                    height: 140
                },
                i);
                c.attr("class", "on")
            }
        })
    });
   
});